mapper.select:
SELECT count(*)
FROM room_users
WHERE room_id = #{roomId}
AND player_id = #{playerId}

SELECT count(*)
FROM room_users
WHERE stream_url = #{value}

mapper.update:
update room_users
    set state = "Progress"
WHERE room_id = #{roomId}

update room_users
    set stream_url = #{streamUrl}
WHERE room_id = #{roomId}
  AND player_id = #{playerId}
